import { Component, OnInit } from '@angular/core';
import {OktaAuthService} from "@okta/okta-angular";
import {MenuService} from "../services/menu.service";
import {Router} from "@angular/router";
import {RecipeService} from "../services/recipe.service";

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit {

  isAuthenticated: boolean = false;
  userFullName: string = "";

  constructor(private oktaAuthService: OktaAuthService,
              private menuService: MenuService,
              private router: Router,
              private recipeService: RecipeService) {
      // this.oktaAuthService.$authenticationState.subscribe(
      //     isAuth => this.isAuthenticated = isAuth
      // );
      //
      // this.oktaAuthService.isAuthenticated().then(
      //     (isAuthenticated) => {
      //         this.isAuthenticated = isAuthenticated;
      //         console.log(isAuthenticated, 'auth');
      //         if (this.isAuthenticated) {
      //             this.oktaAuthService.getUser().then(
      //                 (user) => {
      //                     this.userFullName = user.name || "";
      //                     console.log(user.name);
      //                 }
      //             );
      //         }
      //     }
      // );
  }

  async ngOnInit() {

      // this.isAuthenticated = await this.oktaAuthService.isAuthenticated()

      // if (this.isAuthenticated) {
      //     const userClaims = await this.oktaAuthService.getUser();
      //     this.userFullName = userClaims.name || "";
      //     console.log(userClaims.name);
      // }



    this.oktaAuthService.$authenticationState.subscribe(
        (result) => {
          this.isAuthenticated = result;
          this.getUserDetails();
        }
    )
   }

  private getUserDetails() {
    if (this.isAuthenticated) {
      this.oktaAuthService.getUser().then(
          (res) => {
            this.userFullName = res.name;
            this.menuService.userSubject.next(res.email);
          }
      );
    }
   }

  private logOut() {
      this.recipeService.onDisplayChange.next('select');
      this.oktaAuthService.signOut();
  }

    navigate(url: string) {
      this.recipeService.onDisplayChange.next('select');
      this.router.navigateByUrl(url);
    }
}
<div id="header-container">
    <img src="https://i.pinimg.com/originals/4d/6c/2b/4d6c2bed8d30ee1814949e6522c80009.jpg">
    <nav class="navbar navbar-default" id="my-navbar">
        <div class="container-fluid">
            <!-- Brand and toggle get grouped for better mobile display -->
<!--            <div class="navbar-header">-->
<!--                <button type="button" class="navbar-toggle collapsed" data-toggle="collapse">-->
<!--                    <span class="icon-bar" *ngFor="let iconBar of [1, 2, 3]"></span>-->
<!--                </button>-->
<!--            </div>-->

            <!-- Collect the nav links, forms, and other content for toggling -->
            <div class="collapse navbar-collapse">
                <ul class="nav navbar-nav">
                    <li routerLink="/" class="navbar-brand">Feast Planner</li>
                    <li routerLinkActive="active"><a routerLink="/recipes">Recipes</a></li>
                </ul>

                <ul class="nav navbar-nav navbar-right">
                    <li *ngIf="!isAuthenticated" (click)="navigate('/login')">Login</li>
                    <li *ngIf="isAuthenticated" class="dropdown">
                        <!--                <li class="dropdown">-->
                        <a class="dropdown-toggle" data-toggle="dropdown" role="button" aria-haspopup="true" aria-expanded="false"><span class="glyphicon glyphicon-user"></span> <span class="caret"></span></a>
                        <ul class="dropdown-menu">
                            <li>Signed in as {{userFullName}}</li>
                            <li role="separator" class="divider"></li>
                            <li (click)="navigate('/menus')">Your menus</li>
                            <li role="separator" class="divider"></li>
                            <li (click)="logOut()">Logout</li>
                        </ul>
                    </li>
                </ul>
            </div><!-- /.navbar-collapse -->
        </div><!-- /.container-fluid -->
    </nav>
</div>
